System.out.println(a[3]); // 输出 123  一个例题    Very Simple Arithmetic     题目大意：给你一个简单的式子求出对应的结果。我发现这完全就是为了练习java而做的题目。。     import java.util.; import java.math.; public class Main{ public static void main(String[] args){ Scanner cin = new Scanner(System.in); while ( cin.hasNextBigDecimal() ){ BigDecimal a = cin.nextBigDecimal(); String op = cin.next(); BigDecimal b = cin.nextBigDecimal(); if ( op.equals(&ldquo;+&rdquo;) ){ System.out.println(a.add(b)); }else if ( op.equals(&ldquo;-&rdquo;) ){ System.out.println(a.subtract(b)); }else if ( op.equals(&rdquo;*&ldquo;) ){ System.out.println(a.multiply(b)); }else if ( op.equals(&ldquo;/&rdquo;) ){ int c = cin.nextInt(); BigDecimal res[] = a.divideAndRemainder(b); System.out.println(res[0]+&rdquo; &ldquo;+res[1]+&rdquo; &ldquo;+a.divide(b,c,BigDecimal.ROUND_HALF_UP).toPlainString()); } } } }

