memset(dis,0,sizeof(dis)); memset(order,0,sizeof(order)); memset(pid,0,sizeof(pid)); memset(ed,0,sizeof(ed)); memset(father,0,sizeof(father)); memset(left,0,sizeof(left)); memset(right,0,sizeof(right)); memset(data,0,sizeof(data)); l=0; r=1; que[r]=1; vis[1]=true; dis[1]=1; //维护深度 while (l&amp;lt;r) { q=que[++l]; for (p=last[q];p;p=pre[p]) if (vis[other[p]]==false) { vis[other[p]]=true; que[++r]=other[p]; father[other[p]]=q; //求得他的父亲节点编号 fatherp[other[p]]=p; //当前节点到父亲的边 dis[other[p]]=dis[q]+1; link[(p+1)/2]=other[p]; } } for (i=1;i&amp;lt;=n;i++) s[i]=1; for (i=n;i;i--) s[father[que[i]]]+=s[que[i]]; for (i=1;i&amp;lt;=n;i++) { q=que[i]; for (p=last[q];p;p=pre[p]) if (dis[q]&amp;lt;dis[other[p]]) if (s[other[p]]&amp;gt;maxs[q]) { maxs[q]=s[other[p]]; next[q]=other[p]; } } memset(vis,0,sizeof(vis)); for (i=1;i&amp;lt;=n;i++) { q=que[i]; if (vis[q]==true) continue; id++; tot=0; ed[id]=q; do { a[++tot]=q; vis[q]=true; pid[q]=id; order[q]=tot; q=next[q]; } while (q!=0); build(1,tot,root[id]); } /*if (debug==2) for (i=1;i&amp;lt;=n;i++) if (dis[i]&amp;lt;dis[ed[pid[i]]]) printf(&quot;?&quot;);*/  }
inline int ask(int x,int y) //从x节点一直到祖先y { int ans=-oo; //if (dt==1) printf(&ldquo;%d %d\n&rdquo;,x,y); while (x!=y) { //if (dt==1) printf(&ldquo;x : %d %d %d %d\n&rdquo;,x,y,dis[x],dis[y]); if (dis[ed[pid[x]]]&lt;=dis[y]) //如果在一条链上 { ans=MAX(ans,find(order[y]+1,order[x],root[pid[x]])); break;
} if (dis[ed[pid[x]]]&gt;dis[y]) //如果不在一条链上 { ans=MAX(ans,find(1,order[x],root[pid[x]])); // if (dt==1) printf(&ldquo;wo cao: %d %d\n&rdquo;,dis[x],dis[father[ed[pid[x]]]]); x=father[ed[pid[x]]]; continue;
} } return ans; }
